import React from 'react'

function cartItems(props) {

    return (props.cartItemsList.map((cartItem) =>
        <div key={cartItem.title}>title:{(cartItem.title) ? cartItem.title : 'N/A'} <br />
            price:{(cartItem.price) ? cartItem.price : 'N/A'}
        </div>
    )
    )
}

class AddToCart extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            cartItemsList: []
        }
        console.log("props in addtocart component", props);
        if (this.state.cartItemsList.indexOf(props.shoppingItemDetails.item.title) == -1 && props.shoppingItemDetails.item.title != '' && props.shoppingItemDetails.item.title != null && props.shoppingItemDetails.item.title != undefined) {
            this.state.cartItemsList.push(props.shoppingItemDetails.item);
            this.setState({
                cartItemsList
            })
        }
    }


render() {
    return(
        <div> <cartItems cartItemsList={this.state.cartItemsList} /></div >
        // <ul>{cartItems}</ul>
        // <div>name:{props.shoppingItemDetails.item.title}</div>
    )
}
}

export default AddToCart;
