import PropTypes from 'prop-types'
import './sizes.css'

function Sizes({sizesAvailable, onSizeClick}) {
 console.log("sizesAvailable", sizesAvailable);
 const sizeItems = sizesAvailable.map((size) =>
  <li>{size}
  <span onClick={handleSizeClick}></span>
  </li>
 )
 return (
     <ul>{sizeItems}</ul>
 )
}

export default Sizes;
