import React from 'react'
import { Table } from 'reactstrap'

function CartItems(props) {

    console.log("in cartItems function props", props);

    return (props.cartItemsList.shoppingCartItem.map((cartItem) =>

        <tbody>
            {(cartItem.item.title) ?
                <tr onMouseEnter={props.handleMouseHover}
                    onMouseLeave={props.handleMouseHover}>
                    <td>{(cartItem.item.title) ? cartItem.item.title : ''} </td>
                    <td>{(cartItem.item.price) ? cartItem.item.price : ''}</td>
                    <td>{(cartItem.item.count) ? cartItem.item.count : ''}</td>
                </tr> : ''}
        </tbody>
    )
    )
}

class AddToCart extends React.Component {

    constructor(props) {
        super(props);
        console.log("props in addtocart component", props);
        this.state = {
            isHovering: false
        }
    }

    render() {
        return (
            <div>
                <p>Shopping Cart</p>
                {(this.props.shoppingItemDetails.shoppingCartItem.length > 1) ?

                    <Table responsive hover>
                        <thead>
                            <tr>
                                <th>Item Name</th>
                                <th>Price</th>
                                <th>Quantity</th>
                            </tr>
                        </thead>
                        <CartItems cartItemsList={this.props.shoppingItemDetails} />
                    </Table>
                    : ''}
            </div>
        )
    }
}

export default AddToCart;
