import React from 'react'

function CartItems(props) {

    console.log("in cartItems function props", props);

    return (props.cartItemsList.shoppingCartItem.map((cartItem) =>
        <div>{(cartItem.item.title) ?<div key={cartItem.item.title}>title:{(cartItem.item.title) ? cartItem.item.title : ''} <br />
            price:{(cartItem.item.price) ? cartItem.item.price : ''}
        </div>: ''}</div>
    )
    )
}

class AddToCart extends React.Component {
    constructor(props) {
        super(props);
        console.log("props in addtocart component", props);
    }

    render() {
        return (
            <div> <CartItems cartItemsList={this.props.shoppingItemDetails} /></div >
            // <ul>{cartItems}</ul>
            // <div>name:{props.shoppingItemDetails.item.title}</div>
        )
    }
}

export default AddToCart;
