import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';
import Sizes from './components/sizes.js'
import ShoppingItem from './components/shoppingitem.js'

const sizesAvailable = ["L", "M", "S", "XL", "XM", "XS", "XXL"]
const shoppingItemsData = [
  {
    title: 'Celtics Jersey',
    price: '$15'
  }, {
    title: 'Basketball',
    price: '$12'
  }, {
    title: 'MacBook Pro',
    price: '$2400'
  }
]

class App extends Component {
  constructor(props) {
    super(props);
  }

  handleSizeClick(size) {
    console.log("this is:", this);
  }

  handleShoppingItemCardClick(item) {
    console.log("clicked item is:", item);
  }

  render() {
    return (
      <div>
        <div class="sizes-class">
          <Sizes
            sizesAvailable={sizesAvailable}
            onClick={(size, e) => this.handleSizeClick.bind(size, e)}
          />
        </div>
        <div class="shopping-item-class">
          <ShoppingItem
            shoppingItemsData={shoppingItemsData}
            onClick={(shoppingItemsData, e) => this.handleShoppingItemCardClick.bind(item, e)}
          />
        </div>
      </div>
    );
  }
}

export default App;
