import React from 'react'

const cartItemsList = []

function AddToCart(props) {

    console.log("props in addtocart component", props);

    if (cartItemsList.indexOf(props.shoppingItemDetails.item.title) == -1 && props.shoppingItemDetails.item.title != '') {
        cartItemsList.push(props.shoppingItemDetails.item);
    }

    const cartItems = cartItemsList.map((cartItem) =>
        <div>title:{cartItem.title} <br />
            price:{cartItem.price}
         </div>
    )

    return (
        <ul>{cartItems}</ul>
        // <div>name:{props.shoppingItemDetails.item.title}</div>
    )
}

export default AddToCart;
