import React from 'react'
import { Table } from 'reactstrap'

function CartItems(props) {

    console.log("in cartItems function props", props);

    return (props.cartItemsList.shoppingItemDetails.shoppingCartItem.map((cartItem) =>
    // props.cartItemsList.handleMouseHover
        <tbody>
            {(cartItem.item.title) ?
                <tr key={cartItem.item.title}
                    onMouseEnter={() => {
                            props.cartItemsList.onMouseEnter()
                            hidden.display = "block"
                        }}
                    onMouseLeave={() => {
                        props.cartItemsList.onMouseLeave()
                        hidden.display = "none"
                    }}>
                    <td>{(cartItem.item.title) ? cartItem.item.title : ''} </td>
                    <td>{(cartItem.item.price) ? cartItem.item.price : ''}</td>
                    <td>{(cartItem.item.count) ? cartItem.item.count : ''}</td>
                    <td style={ hidden }>X</td>
                </tr> : ''}
        </tbody>
    )
    )
}

class AddToCart extends React.Component {

    constructor(props) {
        super(props);
        console.log("props in addtocart component", props);
        this.state = {
            display: "none"
        }
    }

    render() {
        return (
            <div>
                <p>Shopping Cart</p>
                {(this.props.shoppingItemDetails.shoppingCartItem.length > 1) ?

                    <Table responsive hover>
                        <thead>
                            <tr>
                                <th>Item Name</th>
                                <th>Price</th>
                                <th>Quantity</th>
                            </tr>
                        </thead>
                        <CartItems cartItemsList={this.props} hidden={this.state.display} onMouseEnter={this.handleRemoveRowButton}/>
                    </Table>
                    : ''}
            </div>
        )
    }
}

export default AddToCart;
